'use strict';

var gulp = global.gulp = global.gulp || require('gulp');
var thirdParty = global.thirdParty = global.thirdParty || require('./third-party.js');

gulp.task('ng-publish', function(done) {
    thirdParty.publish('angular', 'npm install && gulp ng-root-file && npm run scripts && gulp aot-scripts styles-all angular-api', done);
});

gulp.task('ng-root-file', function() {
    var build = require('../tasks/build');
    // generate root files
    build.generateRootFiles('./src');
});